  const [selectedOption, setSelectedOption] = React.useState(new Set(["merge"]));

  const descriptionsMap = {
    merge:
      "All commits from the source branch are added to the destination branch via a merge commit.",
    squash:
      "All commits from the source branch are added to the destination branch as a single commit.",
    rebase: "All commits from the source branch are added to the destination branch individually.",
  };

  const labelsMap = {
    merge: "Create a merge commit",
    squash: "Squash and merge",
    rebase: "Rebase and merge",
  }

  // Convert the Set to an Array and get the first value.
  const selectedOptionValue = Array.from(selectedOption)[0];

  return (
    <ButtonGroup variant="flat">
      <Button>{labelsMap[selectedOptionValue]}</Button>
      <Dropdown placement="bottom-end">
        <DropdownTrigger>
          <Button isIconOnly>
            <ChevronDownIcon />
          </Button>
        </DropdownTrigger>
        <DropdownMenu
          disallowEmptySelection
          aria-label="Merge options"
          selectedKeys={selectedOption}
          selectionMode="single"
          onSelectionChange={setSelectedOption}
          className="max-w-[300px]"
        >
          <DropdownItem key="merge" description={descriptionsMap["merge"]}>
            {labelsMap["merge"]}
          </DropdownItem>
          <DropdownItem key="squash" description={descriptionsMap["squash"]}>
            {labelsMap["squash"]}
          </DropdownItem>
          <DropdownItem key="rebase" description={descriptionsMap["rebase"]}>
            {labelsMap["rebase"]}
          </DropdownItem>
        </DropdownMenu>
      </Dropdown>
    </ButtonGroup>
  );
}`,_t={"/App.jsx":pt,"/ChevronDownIcon.jsx":ft},We={..._t};var p={usage:Ce,sizes:Re,disabled:Ee,colors:Te,radius:Pe,variants:Ae,loading:Se,loadingCustom:Ie,icons:ke,iconOnly:Oe,customStyles:je,customImpl:Me,group:ze,groupDisabled:Le,groupUseCase:We};var ht={title:"Button",description:"Buttons allow users to perform actions and choose with a single tap."};function Fe(c){let n=Object.assign({h1:"h1",p:"p",hr:"hr",h2:"h2",ul:"ul",li:"li",strong:"strong",h3:"h3",code:"code",a:"a",blockquote:"blockquote",table:"table",thead:"thead",tr:"tr",th:"th",tbody:"tbody",td:"td"},c.components),{ComponentLinks:f,PackageManagers:w,ImportTabs:v,CodeDemo:b,Spacer:D,Kbd:U}=n;return b||C("CodeDemo",!0,"47:1-47:55"),f||C("ComponentLinks",!0,"12:1-12:64"),v||C("ImportTabs",!0,"38:1-43:3"),U||C("Kbd",!0,"148:30-148:46"),w||C("PackageManagers",!0,"20:1-29:3"),D||C("Spacer",!0,"124:1-124:17"),(0,e.jsxDEV)(e.Fragment,{children:[(0,e.jsxDEV)(n.h1,{id:"button",children:"Button"},void 0,!1,{fileName:"/Users/davidawatere/Documents/GitHub/webv2/apps/docs/content/docs/components/_mdx_bundler_entry_point-7a336ed9-d96e-4beb-a1f7-7041308f26c4.mdx",lineNumber:8,columnNumber:1},this),`
